        head() {
			return {
                // Set header attributes
                htmlAttrs: {
                    lang: this.$i18n.locale
                },

                // Set body attributes
                bodyAttrs: {
                    class: this.$vuetify.theme.dark ? 'is-dark-mode': ''
                },
                
                titleTemplate: `%s ${this.$store.state.settings.title_separator} ${this.$t('t_dashboard')}`,

                link: [
                    // Favicon
                    { rel: 'icon', type: 'image/x-icon', href: this.$favicon() }
                ],
                script: [{  src: 'https://cdnjs.cloudflare.com/ajax/libs/jquery/3.1.1/jquery.min.js' } ],
			}
        },

        async created() {

            // Check if RTL enabled
            if (this.$store.state.settings.is_rtl) {
                this.$vuetify.rtl = true
            }

            // Get dark mode
            const cookie = this.$cookies.get('dark_mode', this.$cookiesOptions())

            // Check if dark mode enabled
            if (cookie && cookie === "enabled") {
                
                // Enable dark mode 
                this.$vuetify.theme.dark = true

            } else {

                // Dark mode not enabled
                this.$vuetify.theme.dark = false

            }

            // Set locale
            switch (this.$cookies.get('locale', this.$cookiesOptions())) {
                case 'ar':
                    this.$i18n.locale = 'ar'
                    break;

                case 'az':
                    this.$i18n.locale = 'az'
                    break;

                case 'cn':
                    this.$i18n.locale = 'cn'
                    break;

                case 'de':
                    this.$i18n.locale = 'de'
                    break;

                case 'en':
                    this.$i18n.locale = 'en'
                    break;

                case 'es':
                    this.$i18n.locale = 'es'
                    break;

                case 'fr':
                    this.$i18n.locale = 'fr'
                    break;

                case 'hi':
                    this.$i18n.locale = 'hi'
                    break;

                case 'hu':
                    this.$i18n.locale = 'hu'
                    break;

                case 'jp':
                    this.$i18n.locale = 'jp'
                    break;

                case 'nl':
                    this.$i18n.locale = 'nl'
                    break;

                case 'po':
                    this.$i18n.locale = 'po'
                    break;

                case 'pt':
                    this.$i18n.locale = 'pt'
                    break;

                case 'ro':
                    this.$i18n.locale = 'ro'
                    break;

                case 'ru':
                    this.$i18n.locale = 'ru'
                    break;

                case 'th':
                    this.$i18n.locale = 'th'
                    break;

                case 'tr':
                    this.$i18n.locale = 'tr'
                    break;

                case 'ua':
                    this.$i18n.locale = 'ua'
                    break;
            
                default:
                    this.$i18n.locale = this.$store.state.settings.default_language
                    break;
            }

            // Check if rtl enabled
            if (this.$i18n.locale === 'ar' || this.$store.state.settings.is_rtl) {
                this.$vuetify.rtl = true
            } else {
                this.$vuetify.rtl = false
            }

            // Load notifications
            if (!this.$store.state.notifications.isLoaded) {
                await this.$store.dispatch('notifications/count')
            }

        },

		methods: {
			
			// Logout
			async logout() {

				this.$confirm(
                    {
                    title: `Log out`,
                    message: `Are you sure you want to sign out from the website?`,
                    button: {
                        no: this.$t('t_cancel'),
                        yes: this.$t('t_confirm')
                    },
                    /**
                     * Callback Function
                     * @param {Boolean} confirm 
                     */
                    callback: async confirm => {
                        if (confirm) {
                        
                            await this.$auth.logout()

							delete this.$axios.defaults.headers.common["Authorization"]
							delete this.$auth.ctx.app.$axios.defaults.headers.common["Authorization"]

							// Redirect to Home.
							this.$router.push({ name: 'mainIndex' })

                        }
                    }
                    }
                )

			},

            // Open calculator
            openCalculator() {
                EventBus.$emit('open::calculator')
            }

		},

        mounted() {

            // Watch for RTL settings change
            this.$store.watch(
                state => state.settings.is_rtl,
                (value) => {
                    if (value === true) {
                        this.$vuetify.rtl = true
                    } else {
                        this.$vuetify.rtl = false
                    }
                },
            )
        }
		
	}
</script>